import { useEffect } from "react";
import { List, Segmented, Avatar } from 'antd';



function SpotInfo({ info = {spotName: "N/A", spotAddress: "N/A" } }) {

    var data = [];
    var cnt = 1;

    useEffect(() => {
        console.log("SpotInfo component created");
        data.push({order: cnt, name: info.spotName, address: info.spotAddress});
        cnt += 1;
        console.log(data);
    }, [])

    return (
        <List
    style={{padding: '20px'}}
    itemLayout="horizontal"
    dataSource={data}
    renderItem={(item) => (
      <List.Item>
        <List.Item.Meta
          avatar={<Avatar size="small" shape="square" style={{ backgroundColor: '#87d068' }}>{item.order}</Avatar>}
          title={item.name}
        />
      </List.Item>
    )}
  />
        // <div style={{
        //     border: '1px solid black',
        //     padding: '8px',
        //     margin: '8px'
        // }}>
        //     <div><b>{info.spotName}</b></div>
        //     <div>{info.spotAddress}</div>
        // </div>
    );
}

export default SpotInfo
