import React, { useCallback, useState } from 'react';
import PropTypes from 'prop-types';
import { List, Segmented, Avatar, Tabs } from 'antd';
import Map from './Map';

const data = [
  {
    order: 1,
    spot: '경복궁',
  },
  {
    order: 2,
    spot: '블루보틀 삼청 카페',
  },
  {
    order: 3,
    spot: '명동교자 분점',
  },
  {
    order: 4,
    spot: '북촌 박물관',
  },
  {
    order: 5,
    spot: '비텔로소띠',
  },
  {
    order: 6,
    spot: '몰또 이탈리안 에스프레소바',
  },
  {
    order: 7,
    spot: '더 드로잉 룸 바 & 라운지',
  },
];

const data2 = [
  {
    order: 1,
    spot: '롯데월드타워',
  },
  {
    order: 2,
    spot: '매드포갈릭',
  },
  {
    order: 3,
    spot: '프롬하츠커피 롤데월드몰점',
  },
  {
    order: 4,
    spot: '석촌 호수',
  },
  {
    order: 5,
    spot: '주우동',
  },
  {
    order: 6,
    spot: '카페코너574',
  },
];
const data3 = [
  {
    order: 1,
    spot: '코엑스',
  },
  {
    order: 2,
    spot: '농민백암순대 본점',
  },
  {
    order: 3,
    spot: '테라로사 포스코센터점',
  },
  {
    order: 4,
    spot: '와라야키 쿠이신보',
  },
  {
    order: 5,
    spot: '카페 뮬라',
  },
  {
    order: 6,
    spot: '은행골',
  },
];


const PostRoutes = ({ schedules }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false);

    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, []);

    const onClose = useCallback(() => {
        setShowImagesZoom(false);
    }, []);
    
    return (
        <>
        {/* <div style={{padding: '10px'}}>
            <Segmented size='small' 
          options={[
            {
              label: (
                <div style={{ padding: 1, fontSize: '10px' }}>
                  <div>1일차</div>
                  <div>07/23 토</div>
                </div>
              ),
              value: '1일차',
            },
            {
              label: (
                <div style={{ padding: 1, fontSize: '10px'}}>
                  <div>2일차</div>
                  <div>07/24 일</div>
                </div>
              ),
              value: '2일차',
            },
          ]}
        />
        </div> */}

<Tabs defaultActiveKey="1" style={{padding: '20px'}}>
    <Tabs.TabPane tab="221121 1일차" key="1">
    <List
    style={{padding: '10px'}}
    itemLayout="horizontal"
    dataSource={data}
    renderItem={(item) => (
      <List.Item>
        <List.Item.Meta
          avatar={<Avatar size="small" shape="square" style={{ backgroundColor: '#87d068' }}>{item.order}</Avatar>}
          title={item.spot}
        />
      </List.Item>
    )}
  />
    </Tabs.TabPane>
    <Tabs.TabPane tab="221122 2일차" key="2">
          <List
    style={{padding: '10px'}}
    itemLayout="horizontal"
    dataSource={data2}
    renderItem={(item) => (
      <List.Item>
        <List.Item.Meta
          avatar={<Avatar size="small" shape="square" style={{ backgroundColor: '#87d068' }}>{item.order}</Avatar>}
          title={item.spot}
        />
      </List.Item>
    )}
  />
    </Tabs.TabPane>
    <Tabs.TabPane tab="221123 3일차" key="3">
      <List
    style={{padding: '10px'}}
    itemLayout="horizontal"
    dataSource={data3}
    renderItem={(item) => (
      <List.Item>
        <List.Item.Meta
          avatar={<Avatar size="small" shape="square" style={{ backgroundColor: '#87d068' }}>{item.order}</Avatar>}
          title={item.spot}
        />
      </List.Item>
    )}
  />
    </Tabs.TabPane>
  </Tabs>

{/* <List
    style={{padding: '20px'}}
    itemLayout="horizontal"
    dataSource={data}
    renderItem={(item) => (
      <List.Item>
        <List.Item.Meta
          avatar={<Avatar size="small" shape="square" style={{ backgroundColor: '#87d068' }}>{item.order}</Avatar>}
          title={item.spot}
        />
      </List.Item>
    )}
  /> */}
        </>
    )
};

PostRoutes.propTypes = {
    numberOfTrip: PropTypes.number.isRequired,
    TripDates: PropTypes.arrayOf(PropTypes.object),
}

export default PostRoutes;
