import React, { useCallback, useState } from 'react';
import PropTypes from 'prop-types';
import { List, Segmented, Avatar } from 'antd';
import Map from './Map';

const data = [
  {
    order: 1,
    spot: '경복궁',
  },
  {
    order: 2,
    spot: '블루보틀 삼청 카페',
  },
  {
    order: 3,
    spot: '명동교자 분점',
  },
  {
    order: 4,
    spot: '북촌 박물관',
  },
  {
    order: 5,
    spot: '비텔로소띠',
  },
  {
    order: 6,
    spot: '몰또 이탈리안 에스프레소바',
  },
  {
    order: 7,
    spot: '더 드로잉 룸 바 & 라운지',
  },
];


const PostRoutes = ({ schedules }) => {
    const [showImagesZoom, setShowImagesZoom] = useState(false);

    const onZoom = useCallback(() => {
        setShowImagesZoom(true);
    }, []);

    const onClose = useCallback(() => {
        setShowImagesZoom(false);
    }, []);
    
    return (
        <>
        <div style={{padding: '20px'}}>
            <Segmented size='small' 
          options={[
            {
              label: (
                <div style={{ padding: 1, fontSize: '10px' }}>
                  <div>1일차</div>
                  <div>07/23 토</div>
                </div>
              ),
              value: '1일차',
            },
            {
              label: (
                <div style={{ padding: 1, fontSize: '10px'}}>
                  <div>2일차</div>
                  <div>07/24 일</div>
                </div>
              ),
              value: '2일차',
            },
          ]}
        />
        </div>

<List
    style={{padding: '20px'}}
    itemLayout="horizontal"
    dataSource={data}
    renderItem={(item) => (
      <List.Item>
        <List.Item.Meta
          avatar={<Avatar size="small" shape="square" style={{ backgroundColor: '#87d068' }}>{item.order}</Avatar>}
          title={item.spot}
        />
      </List.Item>
    )}
  />
        </>
    )
};

PostRoutes.propTypes = {
    numberOfTrip: PropTypes.number.isRequired,
    TripDates: PropTypes.arrayOf(PropTypes.object),
}

export default PostRoutes;
